#!/bin/sh
su && pacman -Syu && sed -i 's/#RemoteFileSigLevel = Required/RemoteFileSigLevel = Never/g' /etc/pacman.conf && pacman -U https://www.parabola.nu/packages/core/i686/archlinux32-keyring-transition/download/ && pacman -U https://www.parabola.nu/packages/libre/x86_64/parabola-keyring/download && pacman -U https://www.parabola.nu/packages/libre/x86_64/pacman-mirrorlist/download && sed -i 's/RemoteFileSigLevel = Never/#RemoteFileSigLevel = Required/g' /etc/pacman.conf && cp -vr /etc/pacman.d/mirrorlist.pacnew /etc/pacman.d/mirrorlist && sed -i '/[core]/i [libre]' /etc/pacman.conf && sed -i '/[core]/i Include = /etc/pacman.d/mirrorlist' /etc/pacman.conf && sed -i '/[community]/i [nonprism]'/etc/pacman.conf && sed -i '/[community]/i Include = /etc/pacman.d/mirrorlist'/etc/pacman.conf && pacman -Scc && pacman -Syy && pacman-key --refresh && pacman -Suu pacman your-freedom your-privacy && grub-mkconfig -o /boot/grub/grub.cfg || echo " Script failed....:( "
